import { Template } from 'meteor/templating';
import { ReactiveVar } from 'meteor/reactive-var';

import './main.html';

Template.hello.onCreated(function helloOnCreated() {
  // counter starts at 0
  this.counter = new ReactiveVar(0);
});


ContractAddress = "0x6c26271c1996954b437456b9b62543ec63bce5b0";
myAddress = "0x8f215bf78d61d45b0d2055dcd60e7c37651ce0ab";
otherAddress = "0xaD07635Bd5ef027C4bbc44AEe2E7679CdE469998";
StandardTokenABI = 0; 
AegleABI = 0;
bytecode = 0;
Template.hello.helpers({
  counter() {
    let s_contract = web3.eth.contract(StandardTokenABI).at(ContractAddress);
    s_contract.balanceOf(myAddress, callback = function(err, result) {
      console.log("My address original value: " + result.c[0]);
    });
    s_contract.balanceOf(otherAddress, callback = function(err, result) {
      console.log("Other address original value: " + result.c[0]);
    });
    s_contract.call().perscribe();

  },
});

Template.hello.events({
  'click button'(event, instance) {
    // increment the counter when button is clicked
    let s_contract = web3.eth.contract()
  },
});
